FROM kfashion_image
WHERE work_no = #{workNo}

SELECT DISTINCT kfashion_image.work_no, kfashion_work.work_name, CONCAT('/api/v1/kfashion/img/getByteImage?workNo=',kfashion_work.no) file_name, kfashion_work.created_datetime, kfashion_work_history.created_id FROM kfashion_image INNER JOIN kfashion_work_history ON kfashion_image.work_no = kfashion_work_history.work_no INNER JOIN kfashion_work ON kfashion_image.work_no = kfashion_work.no WHERE kfashion_work_history.work_step = 5 AND kfashion_work_history.work_no NOT IN(SELECT kfashion_work_history.work_no FROM kfashion_work_history WHERE kfashion_work_history.work_step = 6)
AND kfashion_work.work_name LIKE CONCAT('%', #{keyword}, '%')
AND kfashion_work_history.created_id = #{createdId} ORDER BY kfashion_image.work_no DESC LIMIT #{pageSize} OFFSET #{startPage}

