SELECT DISTINCT kfashion_work.no AS work_no, kfashion_work.work_name, CONCAT('/api/v1/kfashion/img/getByteImage?workNo=',kfashion_work.no) file_name, kfashion_work.created_datetime, kfashion_work_history.created_id FROM kfashion_work INNER JOIN kfashion_work_history ON kfashion_work.no = kfashion_work_history.work_no WHERE kfashion_work.work_state = 7
AND kfashion_work.work_name LIKE CONCAT('%', #{keyword}, '%')
AND kfashion_work_history.work_step = 8 LIMIT #{pageSize} OFFSET #{startPage}
