AND     question2 = #{question2}
AND     question3 = #{question3}
AND     answer1 = #{answer1}
AND     answer2 = #{answer2}
AND     answer3 = #{answer3}


    AND     name = #{name}


    AND     nickname = #{nickName}


    AND     id = #{id}

SELECT COUNT(*)
FROM  ksearching_user
WHERE   name = #{name}
AND     nickname = #{nickName}
